import { useEffect, useState } from "react";
import axios from 'axios';

function Student() {
  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [fee, setFee] = useState("");
  const [students, setStudents] = useState([]);

  useEffect(() => {
    Load();
  }, []);

  async function Load() {
    try {
      const response = await axios.get("http://127.0.0.1:8000/student");
      setStudents(response.data);
    } catch (error) {
      console.error("Error loading students:", error);
    }
  }

  async function save(event) {
    event.preventDefault();
    try {
      await axios.post("http://127.0.0.1:8000/student", {
        name: name,
        address: address,
        fee: fee
      });
      alert("Student Registered Successfully");
      Load();
    } catch (error) {
      console.error("Error saving student:", error);
      alert("Student Registration Failed");
    }
  }

  async function editStudent(student) {
    setName(student.name);
    setAddress(student.address);
    setFee(student.fee);
  }

  async function deleteStudent(id) {
    try {
      await axios.delete(`http://127.0.0.1:8000/student/${id}`);
      alert("Student deleted Successfully");
      Load();
    } catch (error) {
      console.error("Error deleting student:", error);
      alert("Student deletion Failed");
    }
  }

  async function update(event) {
    event.preventDefault();
    try {
      await axios.put(`http://127.0.0.1:8000/student/${id}`, {
        name: name,
        address: address,
        fee: fee
      });
      alert("Student Updated Successfully");
      Load();
    } catch (error) {
      console.error("Error updating student:", error);
      alert("Student update Failed");
    }
  }

  return (
    <div>
      {/* Your form and table JSX here */}
    </div>
  );
}

export default Student;
