import { useEffect, useState } from "react";
import axios from 'axios';

function Student() {
  const [id, setId] = useState('');
  const [studentid, setStudentId] = useState('');
  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [fee, setFee] = useState("");
  const [department, setDepartment] = useState("");
  const [regNumber, setRegNumber] = useState(""); // New state for RegNumber
  const [students, setUsers] = useState([]);

  useEffect(() => {
    (async () => await Load())();
  }, []);

  async function Load() {
    const result = await axios.get("http://127.0.0.1:8000/student");
    setUsers(result.data);
    console.log(result.data);
  }

  async function save(event) {
    event.preventDefault();
    try {
      await axios.post("http://127.0.0.1:8000/student", {
        regNumber: regNumber, // Include regNumber in the POST request
        studentid: studentid,
        name: name,
        address: address,
        fee: fee,
        department: department,
      });
      alert("Student Registration Successfully");
      setId("");
      setStudentId("");
      setName("");
      setAddress("");
      setFee("");
      setDepartment("");
      setRegNumber(""); // Clear RegNumber after successful registration
      Load();
    } catch (err) {
      alert("Student Registration Failed");
    }
  }

  async function update(event) {
    event.preventDefault();
    try {
      await axios.put("http://127.0.0.1:8000/student/" + id, {
        regNumber: regNumber, // Include regNumber in the PUT request
        studentid: studentid,
        name: name,
        address: address,
        fee: fee,
        department: department,
      });
      alert("Student Updated Successfully");
      setId("");
      setStudentId("");
      setName("");
      setAddress("");
      setFee("");
      setDepartment("");
      setRegNumber(""); // Clear RegNumber after successful update
      Load();
    } catch (err) {
      alert("Student Update Failed");
    }
  }

  return (
    <div>
      <h1>Student Details</h1>
      <div className="container mt-4">
        <form>
          <div className="form-group">
            <label>Student ID</label>
            <input
              type="text"
              className="form-control"
              id="studentid"
              value={studentid}
              onChange={(event) => {
                setStudentId(event.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label>Student ID</label>
            <input
              type="text"
              className="form-control"
              id="regNumber"
              value={regNumber}
              onChange={(event) => {
                setRegNumber(event.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label>Student Name</label>
            <input
              type="text"
              className="form-control"
              id="name"
              value={name}
              onChange={(event) => {
                setName(event.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label>Address</label>
            <input
              type="text"
              className="form-control"
              id="address"
              value={address}
              onChange={(event) => {
                setAddress(event.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label>Fee</label>
            <input
              type="text"
              className="form-control"
              id="fee"
              value={fee}
              onChange={(event) => {
                setFee(event.target.value);
              }}
            />
          </div>
          <div className="form-group">
            <label>Department</label>
            <input
              type="text"
              className="form-control"
              id="department"
              value={department}
              onChange={(event) => {
                setDepartment(event.target.value);
              }}
            />
          </div>
          <div>
            <button className="btn btn-primary mt-4" onClick={save}>
              Register
            </button> 
            <button className="btn btn-warning mt-4" onClick={update}>
              Update
            </button>
          </div>
        </form>
      </div>

      <table className="table table-dark" align="center">
        <thead>
          <tr>
            <th scope="col">Student ID</th>
            <th scope="col">Student Name</th>
            <th scope="col">Address</th>
            <th scope="col">Fee</th>
            <th scope="col">Department</th>
            <th scope="col">Option</th>
          </tr>
        </thead>
        {students.map(function fn(student) {
          return (
            <tbody key={student.id}>
              <tr>
                <th scope="row">{student.id}</th>
                <td>{student.regNumber}</td>
                <td>{student.name}</td>
                <td>{student.address}</td>
                <td>{student.fee}</td>
                <td>{student.department}</td>
                <td>
                  <button
                    type="button"
                    className="btn btn-warning"
                    onClick={() => editStudent(student)}
                  >
                    Edit
                  </button>
                  <button
                    type="button"
                    className="btn btn-danger"
                    onClick={() => deleteStudent(student.id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            </tbody>
          );
        })}
      </table>
    </div>
  );
}

export default Student;
